import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Header.css"; // नीचे CSS अलग दी गई है

const navLinks = [
  { title: "Home", path: "/" },
  {
    title: "About",
    path: "/about",
    submenu: [
      { title: "Our Team", path: "/team" },
      { title: "Testimonial", path: "/testimonial" }
    ]
  },
  { title: "Services", path: "/services" },
  { title: "Programs", path: "/programs" },
  { title: "Events", path: "/events" },
  { title: "Blogs", path: "/blogs" },
  { title: "Contact", path: "/contact" }
];


export default function Header() {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="main-header">
      <div className="header-inner">
        {/* Logo & Name */}
        <div className="header-logo-group">
          <span style={{ color: "#fa4b89", fontWeight: 800, fontSize: 32, fontFamily: "Poppins,sans-serif" }}>Baby</span>
          <span style={{ color: "#425ffd", fontWeight: 800, fontSize: 32, fontFamily: "Poppins,sans-serif" }}>Care</span>
        </div>
        {/* Nav */}
        <nav className={`header-nav${menuOpen ? " show" : ""}`}>
          <ul>
            {navLinks.map(link => (
              <li key={link.title}>
                <Link to={link.path}>{link.title}</Link>
              </li>
            ))}
          </ul>
        </nav>
        {/* Right Group */}
        <div className="header-rights">
          <div className="header-questions">
            <span className="header-phone-icon">
              <i className="fas fa-phone-alt"></i>
            </span>
            <div>
              <div className="header-ques-text" style={{ color: "#fa4b89" }}>Have any questions?</div>
              <div className="header-ques-phone" style={{ color: "#425ffd" }}>Free: + 0123 456 7890</div>
            </div>
          </div>
          <div className="header-search-btn">
            <i className="fas fa-search"></i>
          </div>
          {/* Burger */}
          <button className="header-burger" aria-label="Menu" onClick={() => setMenuOpen(m => !m)}>
            <span /><span /><span />
          </button>
        </div>
      </div>
    </header>
  );
}
