import React from "react";
import "./ExpertTeamSection.css";

// डेमो data (images को public/images/ में रखें)
const teachers = [
  { img: "/assets/images/team/teacher1.jpg", name: "Linda Carlson", role: "English Teacher" },
  { img: "/assets/images/team/teacher2.jpg", name: "Linda Carlson", role: "English Teacher" },
  { img: "/assets/images/team/teacher3.jpg", name: "Linda Carlson", role: "English Teacher" },
  { img: "/assets/images/team/teacher4.jpg", name: "Linda Carlson", role: "English Teacher" }
];

export default function ExpertTeamSection() {
  return (
    <section className="expert-team-section">
      <div className="team-head">
        <div className="team-label">Our Team</div>
        <h2>Meet With Our<br/>Expert Teacher</h2>
      </div>
      <div className="team-card-row">
        {teachers.map((teacher, i) => (
          <div className="team-card" key={i}>
            <div className="team-imgbox">
              <img src={teacher.img} alt={teacher.name} />
              <div className="team-social-box">
                <a href="https://facebook.com/" target="_blank" rel="noopener noreferrer">
                  <i className="fab fa-facebook-f"></i>
                </a>
                <a href="https://twitter.com/" target="_blank" rel="noopener noreferrer">
                  <i className="fab fa-twitter"></i>
                </a>
                <a href="https://instagram.com/" target="_blank" rel="noopener noreferrer">
                  <i className="fab fa-instagram"></i>
                </a>
              </div>
            </div>
            <div className="team-name">{teacher.name}</div>
            <div className="team-role">{teacher.role}</div>
          </div>
        ))}
      </div>
    </section>
  );
}
