import React from "react";
import Slider from "react-slick";
import "./BlogSliderSection.css";

const blogs = [
  {
    img: "/images/blog1.jpg",
    date: "16 July 2025",
    comments: 6,
    author: "Priya Sharma",
    title: "Parenting Tips For Bright Kids",
    desc: "Parenting tips for Gyan Dayini—positive reinforcement, schedule & feedback system, result improvement."
  },
  {
    img: "/images/blog2.jpg",
    date: "11 July 2025",
    comments: 9,
    author: "Vikas Gupta",
    title: "How To Motivate Young Learners",
    desc: "Motivation, activities, diagnostic tests—see BabyCare's methods in GDC's system."
  },
  {
    img: "/images/blog3.jpg",
    date: "7 July 2025",
    comments: 2,
    author: "Neha Yadav",
    title: "Digital Parent Dashboard Benefits",
    desc: "Gyan Dayini Classes is using tech for real-time parent dashboard and fee tracking."
  }
];

const sliderSettings = {
  dots: true,
  infinite: true,
  speed: 600,
  arrows: false,
  slidesToShow: 3,
  slidesToScroll: 1,
  autoplay: true,
  autoplaySpeed: 3500,
  pauseOnHover: true,
  responsive: [
    { breakpoint: 1100, settings: { slidesToShow: 2 } },
    { breakpoint: 700, settings: { slidesToShow: 1 } }
  ]
};

export default function BlogSliderSection() {
  return (
    <section className="blog-slider-section">
      <div className="blog-head">
        <span className="blog-label">Latest News & Blog</span>
        <h2 className="blog-main-title">Our Educational Blog</h2>
      </div>
      <Slider {...sliderSettings} className="blog-cards-slider">
        {blogs.map((b, i) => (
          <div key={i}>
            <div className="blog-card">
              <div className="blog-img-wrap">
                <img src={b.img} alt={b.title} />
                <div className="blog-meta-top">
                  <span><i className="far fa-calendar"></i>{b.date}</span>
                  <span><i className="far fa-comments"></i>Comments ({b.comments})</span>
                </div>
              </div>
              <div className="blog-body">
                <div className="blog-author">by {b.author}</div>
                <div className="blog-title">{b.title}</div>
                <div className="blog-desc">{b.desc}</div>
                <button className="blog-btn">View Details</button>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </section>
  );
}
