import React from "react";

// For icons, use SVG or image imports, here using emoji placeholders for rapid implementation

const cardData = [
  {
    icon: "üéÆ", // Game controller emoji
    title: "Study & Game",
    description: "Study and play together! Personalized support with engaging activities for holistic growth.",
    active: true
  },
  {
    icon: "üî§", // A to Z
    title: "A to Z Programs",
    description: "Comprehensive learning curriculum from alphabet basics to advanced projects.",
    active: false
  },
  {
    icon: "üë©‚Äçüè´", // Teacher
    title: "Expert Teacher",
    description: "Guidance from experienced educators ensuring personal attention and excellence.",
    active: false
  },
  {
    icon: "ü©∫", // Medical/Mental Health
    title: "Mental Health",
    description: "Caring environment for every child's emotional and social well-being.",
    active: false
  }
];

export default function ServicesSection() {
  return (
    <section style={{
      background: "linear-gradient(180deg, #fff1f7 60%, #faebef 100%)",
      padding: "64px 0"
    }}>
      <div style={{ textAlign: "center", marginBottom: 16 }}>
        <div style={{
          color: "#ff6b9c",
          fontWeight: 600,
          fontSize: 22,
          marginBottom: 8,
          letterSpacing: ".05em"
        }}>
          What We Do
        </div>
        <h2 style={{
          fontWeight: 700,
          fontSize: 36,
          color: "#373969",
          margin: 0,
          letterSpacing: "-0.5px"
        }}>
          Thanks To Get Started<br/>With Our School
        </h2>
      </div>

      <div style={{
        display: "flex",
        flexWrap: "wrap",
        justifyContent: "center",
        gap: "38px 32px",
        marginTop: 36
      }}>
        {cardData.map((card, idx) => (
          <div
            key={card.title}
            style={{
              background: card.active ? "#5d75fa" : "#fff",
              color: card.active ? "#fff" : "#373969",
              boxShadow: card.active ? "0 8px 32px #5d75fa1c" : "0 5px 18px #fd9db420",
              border: card.active ? "none" : "1.6px solid #ffb9ce",
              borderRadius: "42px 32px 38px 36px",
              width: 270,
              minHeight: 330,
              padding: 34,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              transform: card.active ? "scale(1.03)" : "none",
              transition: ".27s cubic-bezier(.22,.63,.45,1.31) all"
            }}>
            <div style={{
              fontSize: 54,
              marginBottom: 18,
              background: card.active ? "#fff" : "#faebef",
              color: card.active ? "#5d75fa" : "#fd8aaf",
              width: 74,
              height: 74,
              borderRadius: 32,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: card.active ? "0 3px 22px #fff2" : "0 3px 16px #fd9db422"
            }}>
              {card.icon}
            </div>
            <div style={{
              fontWeight: 600,
              fontSize: 20,
              color: card.active ? "#ff4b89" : "#ff4b89",
              marginBottom: 11
            }}>
              {card.title}
            </div>
            <div style={{
              color: card.active ? "#fff" : "#6b6c7a",
              fontSize: 15,
              fontWeight: 500,
              lineHeight: 1.5,
              marginBottom: 32,
              minHeight: 65,
              textAlign: "center"
            }}>
              {card.description}
            </div>
            <button style={{
              background: card.active ? "#fff" : "#ff79a9",
              color: card.active ? "#5d75fa" : "#fff",
              border: "none",
              borderRadius: "8px",
              padding: "9px 24px",
              fontWeight: 600,
              fontSize: 16,
              cursor: "pointer",
              transition: ".2s"
            }}>
              Read More
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}
