import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import "./Header.css"; // नीचे media queries/code देखें

const navLinks = [
  { title: "Home", path: "/" },
  {
    title: "About",
    path: "/about",
    submenu: [
      { title: "Our Team", path: "/team" },
      { title: "Testimonial", path: "/testimonial" }
    ]
  },
  { title: "Services", path: "/services" },
  { title: "Programs", path: "/programs" },
  { title: "Events", path: "/events" },
  { title: "Blogs", path: "/blogs" },
  { title: "Contact", path: "/contact" }
];

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [aboutOpen, setAboutOpen] = useState(false); // For About submenu (mobile)
  const navigate = useNavigate();
  const location = useLocation();

  // Desktop Dropdown Hover Logic
  const [desktopAbout, setDesktopAbout] = useState(false);

  return (
    <header className="main-header">
      <div className="header-inner">
        <div className="header-logo-group">
          <span className="brand-red">Baby</span>
          <span className="brand-blue">Care</span>
        </div>
        
        {/* DESKTOP Menu */}
        <nav className={`header-nav${menuOpen ? " show" : ""}`}>
          <ul>
            {navLinks.map((link, idx) =>
              link.submenu ? (
                <li
                  key={link.title}
                  className="nav-has-dropdown"
                  onMouseEnter={() => setDesktopAbout(true)}
                  onMouseLeave={() => setDesktopAbout(false)}
                >
                  <button
                    className={`nav-link-btn${location.pathname === link.path ? " active" : ""}`}
                    type="button"
                    aria-haspopup="true"
                    aria-expanded={desktopAbout ? "true" : "false"}
                  >
                    {link.title}
                    <i className={`fas fa-chevron-${desktopAbout ? "up" : "down"}`} style={{ marginLeft: 6, fontSize: 12 }} />
                  </button>
                  <div style={{ display: desktopAbout ? "block" : "none" }} className="nav-dropdown">
                    {link.submenu.map((sublink) => (
                      <Link key={sublink.title} to={sublink.path}>
                        {sublink.title}
                      </Link>
                    ))}
                  </div>
                </li>
              ) : (
                <li key={link.title}>
                  <Link
                    to={link.path}
                    className={location.pathname === link.path ? "active" : ""}
                  >
                    {link.title}
                  </Link>
                </li>
              )
            )}
          </ul>
        </nav>

        {/* Mobile Burger */}
        <button className="header-burger" aria-label="Open menu" onClick={() => setMenuOpen((m) => !m)}>
          <span /><span /><span />
        </button>

        {/* Have any question? + Search + Login */}
        <div className="header-rights">
          <div className="header-questions">
            <span className="header-phone-icon">
              <i className="fas fa-phone-alt"></i>
            </span>
            <div>
              <div className="header-ques-text">Have any questions?</div>
              <div className="header-ques-phone">Free: + 0123 456 7890</div>
            </div>
          </div>
          <button className="header-search-btn" aria-label="Search">
            <i className="fas fa-search"></i>
          </button>
          <button
            className="header-login-btn"
            onClick={() => navigate("/login")}
          >
            Login
          </button>
        </div>
      </div>

      {/* MOBILE DRAWER */}
      {menuOpen && (
        <>
          <div className="mobile-menu-overlay" onClick={() => setMenuOpen(false)} />
          <nav className="mobile-menu-drawer">
            <div className="mobile-menu-header">
              <div className="header-logo-group">
                <span className="brand-red">Baby</span>
                <span className="brand-blue">Care</span>
              </div>
              <button className="menu-close-btn" onClick={() => setMenuOpen(false)}><i className="fas fa-times"></i></button>
            </div>
            <ul className="mobile-nav-links">
              {navLinks.map((link) =>
                link.submenu ? (
                  <li key={link.title}>
                    <button
                      className="mobile-nav-link-btn"
                      onClick={() => setAboutOpen((open) => !open)}
                      aria-haspopup="true"
                      aria-expanded={aboutOpen ? "true" : "false"}
                    >
                      {link.title}
                      <i className={`fas fa-chevron-${aboutOpen ? "up" : "down"}`} style={{ marginLeft: 6, fontSize: 13 }} />
                    </button>
                    {aboutOpen && (
                      <div className="mobile-nav-dropdown">
                        {link.submenu.map((sublink) => (
                          <Link key={sublink.title} to={sublink.path} onClick={() => setMenuOpen(false)}>
                            {sublink.title}
                          </Link>
                        ))}
                      </div>
                    )}
                  </li>
                ) : (
                  <li key={link.title}>
                    <Link to={link.path} onClick={() => setMenuOpen(false)}>{link.title}</Link>
                  </li>
                )
              )}
            </ul>
            {/* Phone/Help Box */}
            <div className="mobile-header-questions">
              <span className="header-phone-icon"><i className="fas fa-phone-alt"></i></span>
              <div>
                <div className="mobile-ques-text">Have any questions?</div>
                <div className="mobile-ques-phone">Free: + 0123 456 7890</div>
              </div>
            </div>
            <button className="mobile-search-btn" aria-label="Search">
              <i className="fas fa-search"></i>
            </button>
          </nav>
        </>
      )}
    </header>
  );
}
